import React from 'react';
import { connect } from 'react-redux';

class Formulaire extends React.Component {

    constructor(){
        super();
        this.state = {
            mysteryNumber : null,
            userNumber : null,
            tour: 0,
            result: null,
            play : false
        }
    }

    handleRestart(e) {
        e.preventDefault()
        this.setState({play : false, result : "los" , tour : 0});
    }

    handleSend(e) {
        e.preventDefault()
        let _result;
        if (this.state.userNumber > this.state.mysteryNumber) {
            _result = "sup";
        }
        else if ( this.state.userNumber < this.state.mysteryNumber) {
            _result = "inf";
        }
        else {
            _result = "equ";
        }
        this.setState ({
            ...this.state,
            tour: this.state.tour + 1,
            result: _result
        });
    }

    handleChange(e) {
       this.setState({ userNumber : e.target.value })     
    }

    displayResult() {
        switch (this.state.result) {
            case "sup":
                return <p>C'est plus petit !</p>;
            case "inf":
                return <p>C'est plus grand !</p>;
            case "equ":
                return <p>C'est gagnÃ© biloute !</p>
            case "los":
                return <p>C'est perdu l'italien !</p>
            default:
                return <p>El Muchachos !</p>
        }
    }

    timeToPlay() {
        if(!this.state.play){
            this.setState({
                mysteryNumber : this._getRandom(), 
                play : true
            }, console.log("Nombre mystere : " + this.state.mysteryNumber));
            return <p>Entrez un nombre entre 0 et 100 compris : </p>;
        }
        return <p>Start Game : </p>;
    }

    _getRandom() {
        const MIN = 0;
        const MAX = 101;
        return Math.trunc((Math.random() * (MAX - MIN)) + MIN)
    }

    render(){
        return(
            <div>
                { this.timeToPlay() }
                <form>
                    <label>
                        <input type="number" onChange={ (e) => this.handleChange(e) }/>
                        <button onClick={ (e) => this.handleSend(e) }>Send</button>
                        <button onClick={ (e) => this.handleRestart(e) }>Restart</button>
                    </label>
                </form>
                { this.displayResult() }
            </div>
        );
    }

}

const mapStateToProps = (state) => {
    return {
      state
    };
  }
export default connect(mapStateToProps)(Formulaire);
