import {ADD_SCORE, EDIT_SCORE} from "../actions/Actions"

const initialState = {
    victory : []
};

function sortVictory(a, b) {
    return a.tour > b.tour ? 1 : a.tour < b.tour ? -1 : 0;
}

export default function reducer (state = initialState, action) {
    switch (action.type) {
        case ADD_SCORE :
            return  {...state , victory: [...state.victory , action.value].sort(sortVictory) }
        case EDIT_SCORE:
            let i = state.victory.length - 1;
            if (state.victory[i].tour < action.value.tour) {
                // if the score mustn't be save
                return state;
            }
            while (state.victory[i].tour > action.value.tour ) {
                i--;
            }
            console.log("pos : ", i);
            for (let j = state.victory.length - 2 ; j < i ; j--) {
              state.victory[j + 1].userName      = state.victory[j].userName;
              state.victory[j + 1].score         = state.victory[j].score;
              state.victory[j + 1].mysteryNumber = state.victory[j].mysteryNumber;
            }
            state.victory[i] = action.value;
            console.log("state modif: ", state);
            return state;
        default:
            return state;
    }
}
