import { NEW_GAME, USER_INPUT, LOSE_GAME } from '../actions/Actions';

const initialState = {
    mysteryNumber: null,
    result: null,
    tour: 0
};

function _getRandom() {
    const MIN = 0;
    const MAX = 101;
    const number = Math.trunc((Math.random() * (MAX - MIN)) + MIN)
    console.log (number)
    return number
}

export default function reducer (state = initialState, action) {
    let nextState;
    switch (action.type) {
        case NEW_GAME:
            console.log("I am in new game");
            nextState = {
                ...state,
                mysteryNumber: _getRandom(),
                tour: 0
            };
            return nextState;
        case USER_INPUT:
            console.log("User Input");

            let _result;
            if (action.value > state.mysteryNumber) {
                console.log("first if");
                _result = "sup";
            }
            else if (action.value < state.mysteryNumber) {
                console.log("second if");
                _result = "inf";
            }
            else {
                console.log("else");
                _result = "equ";
            }
            nextState = {
                ...state,
                tour: state.tour + 1,
                result: _result
            };
            return nextState;

        case LOSE_GAME:
            console.log("LOSE GAMMMMMMMMMME");
            nextState = {
                ...state,
                mysteryNumber: _getRandom(),
                tour: 0,
                result: "los"
            };
            return nextState;
        default:
            return state;
    }
}
