import React from 'react';
import { addScore, editScore } from '../actions/Actions';

export class Game extends React.Component {
    constructor() {
        super();
        this.state = {
            mysteryNumber: null,
            userNumber: null,
            tour: 0,
            result: null,
        };
    }
    handleRestart(e) {
        e.preventDefault();
        this.setState({ play: false, result: "los", tour: 0 });
    }
    handleSend(e) {
        e.preventDefault();
        this.setState({ mysteryNumber: parseInt(e.target[0].value) });
        let _result;
        if (this.state.userNumber > this.state.mysteryNumber) {
            _result = "sup";
        }
        else if (this.state.userNumber < this.state.mysteryNumber) {
            _result = "inf";
        }
        else {
            _result = "equ";
            if (this.props.state.victory.size() < 5) {
                this.props.state.dispatch(addScore({ userName: this.state.user, tour: this.state.tour, mysteryNumber: this.state.mysteryNumber }));
            }
            else {
                this.props.state.dispatch(editScore({ userName: this.state.user, tour: this.state.tour, mysteryNumber: this.state.mysteryNumber }));
            }
        }
        this.setState({
            ...this.state,
            tour: this.state.tour + 1,
            result: _result
        });
    }
    displayResult() {
        console.log("mystere : " + this.state.mysteryNumber);
        switch (this.state.result) {
            case "sup":
                return <p>C'est plus petit !</p>;
            case "inf":
                return <p>C'est plus grand !</p>;
            case "equ":
                return <p>C'est gagné biloute !</p>;
            case "los":
                return <p>C'est perdu l'italien !</p>;
            default:
                return <p>El Muchachos !</p>;
        }
    }
    timeToPlay() {
        if (!this.state.play) {
            this.setState({
                mysteryNumber: this._getRandom(),
                play: true
            });
            return <p>Entrez un nombre entre 0 et 100 compris : </p>;
        }
        return <p>Start Game : </p>;
    }
    _getRandom() {
        const MIN = 0;
        const MAX = 101;
        return Math.trunc((Math.random() * (MAX - MIN)) + MIN);
    }
    render() {
        return (<div>
            {this.timeToPlay()}
            <form onSubmit={(e) => this.handleSend(e)}>
                <label>
                    <input type="number" disabled={this.state.user} />
                    <button>Send</button>
                </label>
            </form>

            <button onClick={(e) => this.handleRestart(e)}>Restart</button>
            {this.displayResult()}
        </div>);
    }
}

const mapStateToProps = (state) => {
    return {
      state
    };
  }
export default connect(mapStateToProps)(Game);
