import React from 'react';
import { connect } from 'react-redux';
import { userInput, newGame, loseGame } from '../actions/Actions';

class Formulaire extends React.Component {

    constructor(){
        super();
        this.state = {
            userNumber : null,
            play : true
        }

    }


    handleRestart(e) {
        e.preventDefault()
        this.props.dispatch(loseGame());
    }

    handleSend(e) {
        e.preventDefault()
        this.props.dispatch(userInput(this.state.userNumber));
    }

    handleChange(e) {

       this.setState({  userNumber : e.target.value })     
    }

    startGame(e) {
        e.preventDefault()
        this.setState({play : false})
        this.props.dispatch(newGame()); 
     }



    render(){
      console.log("render")
        return(
            <div>
                <form>
                    { this.props.state.mysteryNumber == null ?  <p>Init Game</p> : <p>Entrez un nombre entre 0 et 100 compris : </p> }
                    <label>
                        <input type="number" onChange={ (e) => this.handleChange(e) } disabled={this.state.play}/>
                        <button onClick={ (e) => this.handleSend(e) }>Send</button>
                        <button onClick={ (e) => this.handleRestart(e) }>Restart</button>
                        <button onClick={ (e) => this.startGame(e)} > Start game </button>
                    </label>
                </form>
            </div>
        );
    }

    componentDidMount() {
        console.log(this.props);
    }
}

const mapStateToProps = (state) => {
    return {
      state
    };
  }
  export default connect(mapStateToProps)(Formulaire);
