import React from 'react';
import { connect } from 'react-redux';
import { addScore, editScore } from '../actions/Actions';


 class Formulaire extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            mysteryNumber : null,
            tour: 0,
            result: null
        }
    }

    handleRestart(e) {
        e.preventDefault()
        this.setState({ result : "los" });
    }

    handleSend(e) {
        e.preventDefault();
        let _result;


        let userNumber = parseInt(e.target[0].value);
        if (userNumber > this.state.mysteryNumber) {
            _result = "sup";
        }
        else if (userNumber < this.state.mysteryNumber) {
            _result = "inf";
        }
        else {
            _result = "equ";
            if (this.props.state.victory.size() < 5) {
                this.props.state.dispatch( addScore({ userName: this.props.pseudo, tour: this.state.tour, mysteryNumber: this.state.mysteryNumber }) );
            }
            else {
                this.props.state.dispatch( editScore({ userName: this.props.pseudo, tour: this.state.tour, mysteryNumber: this.state.mysteryNumber }) );
            }
        }
        this.setState ({
            ...this.state,
            tour: this.state.tour + 1,
            result: _result
        });
    }

    displayResult() {
        console.log("state : ", this.state);
        console.log("props : ", this.props);
        console.log("mystere : ", this.state.mysteryNumber);
        switch (this.state.result) {
            case "sup":
                return <p>C'est plus petit !</p>;
            case "inf":
                return <p>C'est plus grand !</p>;
            case "equ":
                return <p>C'est gagné biloute !</p>
            case "los":
                return <p>C'est perdu l'italien !</p>
            default:
                return <p>El Muchachos !</p>
        }
    }

    timeToPlay() {
        if(!this.state.play){
            this.setState({
                mysteryNumber : this._getRandom(), 
                play : true,
                tour : 0
            });
            return <p>Entrez un nombre entre 0 et 100 compris : </p>;
        }
        return <p>Start Game : </p>;
    }

    _getRandom() {
        const MIN = 0;
        const MAX = 101;
        return Math.trunc((Math.random() * (MAX - MIN)) + MIN)
    }

    render(){
        return(
            <div>
                { this.timeToPlay() }
                { this.displayResult() }

                <form onSubmit={ (e) => this.handleSend(e) }>
                    <label>
                        <input type="number" />
                        <button >Send</button>
                    </label>
                </form>

                <button onClick={ (e) => this.handleRestart(e) }>Restart</button>
            </div>
        );
    }

}

const mapStateToProps = (state) => {
    return {
      state
    };
  }
export default connect(mapStateToProps)(Formulaire);
