import React from 'react';


export default class Formulaire extends React.Component {

    constructor(){
        super()
        this.state = {
            mysteryNumber : null,
            numChoose : null,
            play : false
        }
    }

    justPrice = (e) =>{
        e.preventDefault(),
        this.setState({mysteryNumber : Math.random(0,100), play : true})

    }

    render(){
        return(
            <div>
                <form>
                    {this.state.play ? <p>Jouer</p> : <p>Entrez un nombre entre 0 et 100 compris : </p>}
                    <label>
                        <input type="number"  />
                        <button onClick = {this.justPrice}>Send</button>
                        <button>Restart</button>
                    </label>
                </form>
            </div>
        )
    }


}
