import React from 'react';
import { connect } from 'react-redux';

class Affichage extends React.Component {

    render(){
        switch (this.props.result) {
            case "sup":
                return <p>C'est plus petit !</p>;
            case "inf":
                return <p>C'est plus grand !</p>;
            case "equ":
                return <p>C'est gagn√© biloute !</p>
            case "los":
                return <p>C'est perdu l'italien !</p>
            default:
                return <p>El Muchachos !</p>
        }
    }

    componentDidMount() {
        console.log(this.props);
    }
}

const mapStateToProps = (state) => {
    return {
      state
    };
}
export default connect(mapStateToProps)(Affichage);
