import React from 'react';


export default class Formulaire extends React.Component {

    render(){
        return(
            <div>
                <form>
                    { this.state.play ? <p>Jouer</p> : <p>Entrez un nombre entre 0 et 100 compris : </p>}
                    <label>
                        <input type="number"  />
                        <button onClick = {this.justPrice}>Send</button>
                        <button>Restart</button>
                    </label>
                </form>
            </div>
        )
    }


}
