import React from 'react';
import { connect } from 'react-redux';
import { userInput, newGame, loseGame } from '../actions/Actions';

class Formulaire extends React.Component {

    constructor(){
        super();
        this.state = {
            userNumber : null
        }
        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleClick.bind(this);
    }


    handleRestart(e) {
        e.preventDefault()
        this.props.dispatch(loseGame());
    }

    handleSend(e) {
        e.preventDefault()
        this.props.dispatch(userInput(this.state.userNumber));
    }

    handleChange(e) {
       this.setState({  userNumber : e.target.value })     
    }



    render(){

        return(
            <div>
                <form>
                    { this.props.state.result  ?   <p>Entrez un nombre entre 0 et 100 compris : </p> : <p>Jouer</p>}
                    <label>
                        <input type="number" onChange={ (e) => this.handleChange(e) } />
                        <button onClick={ (e) => this.handleSend(e) }>Send</button>
                        <button onClick={ (e) => this.handleRestart(e) }>Restart</button>
                    </label>
                </form>
            </div>
        );
    }

    componentDidMount() {
        console.log(this.props);
    }
}

const mapStateToProps = (state) => {
    return {
      state
    };
  }
  export default connect(mapStateToProps)(Formulaire);
