import React from 'react';
import { connect } from 'react-redux';

class Formulaire extends React.Component {

    constructor(){
        super();
        this.state = {
            mysteryNumber : null,
            userNumber : null,
            tour: 0,
            result: null,
            play : false
        }
    }


    handleRestart(e) {
        e.preventDefault()
        this.setState({play : false, result : "los"});


    }

    handleSend(e) {
        e.preventDefault()
        let _result;
            if (this.state.userNumber > this.state.mysteryNumber) {
                _result = "sup";
            }
            else if ( this.state.userNumber < this.state.mysteryNumber) {
                _result = "inf";
            }
            else {
                _result = "equ";
            }
            this.setState ( {

                ...this.state,
                tour: this.state.tour + 1,
                result: _result

            });
            console.log (this.state.tour);
    }

    handleChange(e) {
       this.setState({ userNumber : e.target.value })     
    }

    startGame(e) {
        e.preventDefault()
    }

    displayResult() {
        switch (this.state.result) {
            case "sup":
                return <p>C'est plus petit !</p>;
            case "inf":
                return <p>C'est plus grand !</p>;
            case "equ":
                return <p>C'est gagnÃ© biloute !</p>
            case "los":
                return <p>C'est perdu l'italien !</p>
            default:
                return <p>El Muchachos !</p>
        }
    }

    timeToPlay() {
        if(!this.state.play){
            this.setState({mysteryNumber : this._getRandom(), play : true});
        }
    }

    _getRandom() {
        const MIN = 0;
        const MAX = 101;
        const number = Math.trunc((Math.random() * (MAX - MIN)) + MIN)
        console.log (number)
        return number
    }

    render(){
      console.log("render")
        return(
            <div>
                { this.timeToPlay() }
                <form>
                    { this.state.mysteryNumber == null ?  <p>Init Game</p> : <p>Entrez un nombre entre 0 et 100 compris : </p> }
                    <label>
                        <input type="number" onChange={ (e) => this.handleChange(e) }/>
                        <button onClick={ (e) => this.handleSend(e) }>Send</button>
                        <button onClick={ (e) => this.handleRestart(e) }>Restart</button>
                    </label>
                </form>
                { this.displayResult() }
            </div>
        );
    }

}

const mapStateToProps = (state) => {
    return {
      state
    };
  }
export default connect(mapStateToProps)(Formulaire);
